    private db: sqlite3.Database;

    constructor(dbPath: string = './grimoire.db') {
        this.db = new sqlite3.Database(dbPath);
        this.initializeTables();
    }

    private initializeTables(): void {
        this.db.serialize(() => {
            // Create spells table
            this.db.run(`
                CREATE TABLE IF NOT EXISTS spells (
                    id TEXT PRIMARY KEY,
                    name TEXT NOT NULL,
                    convocation TEXT NOT NULL,
                    complexity_level INTEGER NOT NULL,
                    description TEXT NOT NULL,
                    bonus_effects TEXT NOT NULL,
                    casting_time TEXT NOT NULL,
                    range TEXT NOT NULL,
                    duration TEXT NOT NULL
                )
            `);

            // Create characters table
            this.db.run(`
                CREATE TABLE IF NOT EXISTS characters (
                    id TEXT PRIMARY KEY,
                    name TEXT NOT NULL,
                    occupation TEXT NOT NULL,
                    game TEXT NOT NULL
                )
            `);

            // Create character_spells junction table for many-to-many relationship
            this.db.run(`
                CREATE TABLE IF NOT EXISTS character_spells (
                    character_id TEXT,
                    spell_id TEXT,
                    PRIMARY KEY (character_id, spell_id),
                    FOREIGN KEY (character_id) REFERENCES characters(id) ON DELETE CASCADE,
                    FOREIGN KEY (spell_id) REFERENCES spells(id) ON DELETE CASCADE
                )
            `);
        });
    }

    // Spell methods
    getAllSpells(): Promise<Spell[]> {
        return new Promise((resolve, reject) => {
            this.db.all('SELECT * FROM spells', (err, rows: any[]) => {
                if (err) reject(err);
                else resolve(rows.map(this.rowToSpell));
            });
        });
    }

    getSpellById(id: string): Promise<Spell | undefined> {
        return new Promise((resolve, reject) => {
            this.db.get('SELECT * FROM spells WHERE id = ?', [id], (err, row: any) => {
                if (err) reject(err);
                else resolve(row ? this.rowToSpell(row) : undefined);
            });
        });
    }

    addSpell(spell: Spell): Promise<void> {
        return new Promise((resolve, reject) => {
            const stmt = this.db.prepare(`
                INSERT INTO spells (id, name, convocation, complexity_level, description, bonus_effects, casting_time, range, duration)
                VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
            `);
            stmt.run([
                spell.id,
                spell.name,
                spell.convocation,
                spell.complexityLevel,
                spell.description,
                JSON.stringify(spell.bonusEffects),
                spell.castingTime,
                spell.range,
                spell.duration
            ], (err) => {
                if (err) reject(err);
                else resolve();
            });
            stmt.finalize();
        });
    }

    // Character methods
    getAllCharacters(): Promise<Character[]> {
        return new Promise((resolve, reject) => {
            this.db.all('SELECT * FROM characters', async (err, rows: any[]) => {
                if (err) reject(err);
                else {
                    const characters = await Promise.all(
                        rows.map(async (row) => await this.rowToCharacter(row))
                    );
                    resolve(characters);
                }
            });
        });
    }

    getCharacterById(id: string): Promise<Character | undefined> {
        return new Promise((resolve, reject) => {
            this.db.get('SELECT * FROM characters WHERE id = ?', [id], async (err, row: any) => {
                if (err) reject(err);
                else resolve(row ? await this.rowToCharacter(row) : undefined);
            });
        });
    }

    addCharacter(character: Character): Promise<void> {
        return new Promise((resolve, reject) => {
            this.db.serialize(() => {
                const stmt = this.db.prepare(`
                    INSERT INTO characters (id, name, occupation, game)
                    VALUES (?, ?, ?, ?)
                `);
                stmt.run([character.id, character.name, character.occupation, character.game], (err) => {
                    if (err) {
                        reject(err);
                        return;
                    }

                    // Add spell relationships
                    if (character.knownSpellIds.length > 0) {
                        const spellStmt = this.db.prepare('INSERT INTO character_spells (character_id, spell_id) VALUES (?, ?)');
                        character.knownSpellIds.forEach(spellId => {
                            spellStmt.run([character.id, spellId]);
                        });
                        spellStmt.finalize();
                    }
                    resolve();
                });
                stmt.finalize();
            });
        });
    }

    getSpellsForCharacter(characterId: string): Promise<Spell[]> {
        return new Promise((resolve, reject) => {
            const query = `
                SELECT s.* FROM spells s
                JOIN character_spells cs ON s.id = cs.spell_id
                WHERE cs.character_id = ?
            `;
            this.db.all(query, [characterId], (err, rows: any[]) => {
                if (err) reject(err);
                else resolve(rows.map(this.rowToSpell));
            });
        });
    }

    private rowToSpell(row: any): Spell {
        return {
            id: row.id,
            name: row.name,
            convocation: row.convocation as SpellConvocation,
            complexityLevel: row.complexity_level,
            description: row.description,
            bonusEffects: JSON.parse(row.bonus_effects) as BonusEffect[],
            castingTime: row.casting_time,
            range: row.range,
            duration: row.duration
        };
    }

    private async rowToCharacter(row: any): Promise<Character> {
        const knownSpellIds = await this.getKnownSpellIds(row.id);
        return {
            id: row.id,
            name: row.name,
            occupation: row.occupation,
            game: row.game,
            knownSpellIds
        };
    }

    private getKnownSpellIds(characterId: string): Promise<string[]> {
        return new Promise((resolve, reject) => {
            this.db.all(
                'SELECT spell_id FROM character_spells WHERE character_id = ?',
                [characterId],
                (err, rows: any[]) => {
                    if (err) reject(err);
                    else resolve(rows.map(row => row.spell_id));
                }
            );
        });
    }

    close(): void {
        this.db.close();
    }
}

export const database = new Database();
````

Now let's update the data store to use the SQLite database:

````typescript path=apps/backend/data/store.ts mode=EDIT
import { Spell } from '../models/Spell.js';
import { Character } from '../models/Character.js';
import { database } from './database.js';

export class DataStore {
    // Spell methods
    async getAllSpells(): Promise<Spell[]> {
        return await database.getAllSpells();
    }

    async getSpellById(id: string): Promise<Spell | undefined> {
        return await database.getSpellById(id);
    }

    async addSpell(spell: Spell): Promise<void> {
        return await database.addSpell(spell);
    }

    // Character methods
    async getAllCharacters(): Promise<Character[]> {
        return await database.getAllCharacters();
    }

    async getCharacterById(id: string): Promise<Character | undefined> {
        return await database.getCharacterById(id);
    }

    async addCharacter(character: Character): Promise<void> {
        return await database.addCharacter(character);
    }

    // Relationship methods
    async getSpellsForCharacter(characterId: string): Promise<Spell[]> {
        return await database.getSpellsForCharacter(characterId);
    }
}

export const dataStore = new DataStore();
````

Finally, let's update the API endpoints to handle async operations:

````typescript path=apps/backend/index.ts mode=EDIT
import express from 'express';
import cors from 'cors';
import { dataStore } from './data/store.js';

const app = express();
app.use(cors());
app.use(express.json());

// Basic health check
app.get('/api/health', (req, res) => {
    res.json({ message: 'HarnMaster Grimoire API is running' });
});

// Spell routes
app.get('/api/spells', async (req, res) => {
    try {
        const spells = await dataStore.getAllSpells();
        res.json({ spells });
    } catch (error) {
        res.status(500).json({ error: 'Failed to fetch spells' });
    }
});

app.get('/api/spells/:id', async (req, res) => {
    try {
        const spell = await dataStore.getSpellById(req.params.id);
        if (spell) {
            res.json(spell);
        } else {
            res.status(404).json({ error: 'Spell not found' });
        }
    } catch (error) {
        res.status(500).json({ error: 'Failed to fetch spell' });
    }
});

// Character routes
app.get('/api/characters', async (req, res) => {
    try {
        const characters = await dataStore.getAllCharacters();
        res.json({ characters });
    } catch (error) {
        res.status(500).json({ error: 'Failed to fetch characters' });
    }
});

app.get('/api/characters/:id', async (req, res) => {
    try {
        const character = await dataStore.getCharacterById(req.params.id);
        if (character) {
            res.json(character);
        } else {
            res.status(404).json({ error: 'Character not found' });
        }
    } catch (error) {
        res.status(500).json({ error: 'Failed to fetch character' });
    }
});

app.get('/api/characters/:id/spells', async (req, res) => {
    try {
        const spells = await dataStore.getSpellsForCharacter(req.params.id);
        res.json({ spells });
    } catch (error) {
        res.status(500).json({ error: 'Failed to fetch character spells' });
    }
});

const PORT = 3000;
app.listen(PORT, () => {
    console.log(`HarnMaster Grimoire API running on http://localhost:${PORT}`);
});
````

