export interface Character {
  id: string
  name: string
  occupation: string
  game: string
  knownSpellIds: string[]
}

export interface Spell {
  id: string
  name: string
  convocation: string
  complexityLevel: number
  description: string
  bonusEffects: BonusEffect[]
  castingTime: string
  range: string
  duration: string
}

export interface BonusEffect {
  masteryLevelMinimum: number
  effectsDescription: string
}
```
