```tsx
import { Spell } from '../types'
import './SpellCard.css'

interface SpellCardProps {
  spell: Spell
}

export function SpellCard({ spell }: SpellCardProps) {
  return (
    <div className="spell-card">
      <div className="spell-header">
        <h3 className="spell-name">{spell.name}</h3>
        <div className="spell-badges">
          <span className="convocation-badge">{spell.convocation}</span>
          <span className="complexity-badge">Level {spell.complexityLevel}</span>
        </div>
      </div>

      <div className="spell-content">
        <div className="spell-description">
          {spell.description}
        </div>

        <div className="spell-details">
          <div className="detail-item">
            <strong>Casting Time:</strong> {spell.castingTime}
          </div>
          <div className="detail-item">
            <strong>Range:</strong> {spell.range}
          </div>
          <div className="detail-item">
            <strong>Duration:</strong> {spell.duration}
          </div>
        </div>
      </div>
    </div>
  )
}
