import { Character } from '../types'
import './Sidebar.css'

interface SidebarProps {
  characters: Character[]
  selectedCharacter: Character | null
  onCharacterSelect: (character: Character) => void
  onCharactersChange: () => void
  loading: boolean
}

export function Sidebar({ 
  characters, 
  selectedCharacter, 
  onCharacterSelect, 
  onCharactersChange,
  loading 
}: SidebarProps) {
  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2>Characters</h2>
        <div className="character-actions">
          <button className="action-btn" title="Add Character">+</button>
          <button className="action-btn" title="Edit Character">✏️</button>
          <button className="action-btn" title="Delete Character">️</button>
        </div>
      </div>

      <div className="character-list">
        {loading ? (
          <div className="loading">Loading characters...</div>
        ) : characters.length === 0 ? (
          <div className="empty-state">No characters found</div>
        ) : (
          characters.map(character => (
            <div
              key={character.id}
              className={`character-item ${selectedCharacter?.id === character.id ? 'selected' : ''}`}
              onClick={() => onCharacterSelect(character)}
            >
              <div className="character-name">{character.name}</div>
              <div className="character-occupation">{character.occupation}</div>
            </div>
          ))
        )}
      </div>
    </div>
  )
}
