Path: apps/frontend/src/components/MainContent.tsx
     1	import { useState, useEffect } from 'react'
     2	import { Character, Spell } from '../types'
     3	import { SpellCard } from './SpellCard'
     4	
     5	interface MainContentProps {
     6	  selectedCharacter: Character | null
     7	}
     8	
     9	export function MainContent({ selectedCharacter }: MainContentProps) {
    10	  const [spells, setSpells] = useState<Spell[]>([])
    11	  const [filteredSpells, setFilteredSpells] = useState<Spell[]>([])
    12	  const [searchTerm, setSearchTerm] = useState('')
    13	  const [selectedConvocation, setSelectedConvocation] = useState<string>('all')
    14	  const [selectedComplexity, setSelectedComplexity] = useState<string>('all')
    15	  const [loading, setLoading] = useState(false)
    16	
    17	  const convocations = ['Lyahvi', 'Peleahn', 'Jmorvi', 'Fyvria', 'Odivshe', 'Savorya', 'Neutral']
    18	
    19	  useEffect(() => {
    20	    if (selectedCharacter) {
    21	      fetchCharacterSpells(selectedCharacter.id)
    22	    } else {
    23	      setSpells([])
    24	      setFilteredSpells([])
    25	    }
    26	  }, [selectedCharacter])
    27	
    28	  useEffect(() => {
    29	    filterSpells()
    30	  }, [spells, searchTerm, selectedConvocation, selectedComplexity])
    31	
    32	  const fetchCharacterSpells = async (characterId: string) => {
    33	    setLoading(true)
    34	    try {
    35	      const response = await fetch(`/api/characters/${characterId}/spells`)
    36	      const data = await response.json()
    37	      setSpells(data.spells || [])
    38	    } catch (error) {
    39	      console.error('Failed to fetch character spells:', error)
    40	      setSpells([])
    41	    } finally {
    42	      setLoading(false)
    43	    }
    44	  }
    45	
    46	  const filterSpells = () => {
    47	    let filtered = spells
    48	
    49	    if (searchTerm) {
    50	      filtered = filtered.filter(spell =>
    51	        spell.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    52	        spell.description.toLowerCase().includes(searchTerm.toLowerCase())
    53	      )
    54	    }
    55	
    56	    if (selectedConvocation !== 'all') {
    57	      filtered = filtered.filter(spell => spell.convocation === selectedConvocation)
    58	    }
    59	
    60	    if (selectedComplexity !== 'all') {
    61	      filtered = filtered.filter(spell => spell.complexityLevel.toString() === selectedComplexity)
    62	    }
    63	
    64	    setFilteredSpells(filtered)
    65	  }
    66	
    67	  return (
    68	    <div className="flex-1 flex flex-col">
    69	      <div className="p-4 border-b border-gray-700 flex gap-4 items-center flex-wrap">
    70	        <div className="flex-1 min-w-48">
    71	          <input
    72	            type="text"
    73	            placeholder="Search spells..."
    74	            value={searchTerm}
    75	            onChange={(e) => setSearchTerm(e.target.value)}
    76	            className="w-full px-3 py-2 bg-gray-800 border border-gray-600 rounded text-white text-sm focus:outline-none focus:border-blue-500"
    77	          />
    78	        </div>
    79	        
    80	        <div className="flex gap-2">
    81	          <select
    82	            value={selectedConvocation}
    83	            onChange={(e) => setSelectedConvocation(e.target.value)}
    84	            className="px-3 py-2 bg-gray-800 border border-gray-600 rounded text-white text-sm focus:outline-none focus:border-blue-500"
    85	          >
