   426			/*
   427			 * This endpoint deletes an existing Super Blog Clone.  The request must include APPNAME and PERSONA.
   428			 * The endpoint deletes the blog entirely and this action may not be undone.  Removes Wordpress installation
   429			 * from the server filesystem, the Wordpress database, as well as its entry from RFSB `blog` table.
   430			 */
   431			protected function delete() {
   432				$this->logger->log(__CLASS__,__FUNCTION__,$this->runid,"/blog/" . __FUNCTION__ . " received a request ========================================");
   433				// Only accept POST requests.
   434				if ($this->method != 'POST') {
   435					$this->logger->log(__CLASS__,__FUNCTION__,$this->runid,"WARNING: Request received with HTTP {$this->method} method. Only POST allowed. Aborting.");
   436					return ['action' => __FUNCTION__, 'status' => 'error', 'meta' => 'This endpoint only accepts POST requests', 'data' => ''];
   437				}
   438				// Check request parameters
   439				if (
   440					!array_key_exists('appname',$this->request) ||
   441					!array_key_exists('persona',$this->request) ||
   442					empty($this->request['appname']) ||
   443					empty($this->request['persona'])
   444				) {
   445					$missingParamsString = implode(', ',array_diff(['appname','persona'],array_keys($this->request)));
   446					$this->logger->log(__CLASS__,__FUNCTION__,$this->runid,"WARNING: Missing or empty parameter(s): $missingParamsString. Aborting.");
   447					return [
   448						'action' => __FUNCTION__,
   449						'status' => 'error',
   450						'meta' => 'Missing parameters.',
   451						'data' => []
   452					];
   453				}
