     1	import { useState, useEffect } from 'react'
     2	import { Sidebar } from './components/Sidebar'
     3	import { MainContent } from './components/MainContent'
     4	import { Character } from './types'
     5	import './App.css'
     6	
     7	function App() {
     8	  const [characters, setCharacters] = useState<Character[]>([])
     9	  const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null)
    10	  const [loading, setLoading] = useState(false)
    11	
    12	  useEffect(() => {
    13	    fetchCharacters()
    14	  }, [])
    15	
    16	  const fetchCharacters = async () => {
    17	    setLoading(true)
    18	    try {
    19	      const response = await fetch('/api/characters')
    20	      const data = await response.json()
    21	      setCharacters(data.characters || [])
    22	    } catch (error) {
    23	      console.error('Failed to fetch characters:', error)
    24	      setCharacters([])
    25	    } finally {
    26	      setLoading(false)
    27	    }
    28	  }
    29	
    30	  return (
    31	    <div className="flex h-screen bg-gray-900 text-white">
    32	      <Sidebar 
    33	        characters={characters}
    34	        selectedCharacter={selectedCharacter}
    35	        onCharacterSelect={setSelectedCharacter}
    36	        onCharactersChange={fetchCharacters}
    37	        loading={loading}
    38	      />
    39	      <MainContent selectedCharacter={selectedCharacter} />
    40	    </div>
    41	  )
    42	}
    43	
    44	export default App
