Path: apps/backend/index.ts
     1	// apps/backend/index.ts
     2	import express from 'express';
     3	import cors from 'cors';
     4	import { dataStore } from './data/store.js';
     5	
     6	const app = express();
     7	app.use(cors());
     8	app.use(express.json());
     9	
    10	// Basic health check
    11	app.get('/api/health', (req, res) => {
    12	    res.json({ message: 'HarnMaster Grimoire API is running' });
    13	});
    14	
    15	// Spell routes
    16	app.get('/api/spells', async (req, res) => {
    17	    try {
    18	        const spells = await dataStore.getAllSpells();
    19	        res.json({ spells });
    20	    } catch (error) {
    21	        res.status(500).json({ error: 'Failed to fetch spells' });
    22	    }
    23	});
    24	
    25	app.get('/api/spells/:id', async (req, res) => {
    26	    try {
    27	        const spell = await dataStore.getSpellById(req.params.id);
    28	        if (spell) {
    29	            res.json(spell);
    30	        } else {
    31	            res.status(404).json({ error: 'Spell not found' });
    32	        }
    33	    } catch (error) {
    34	        res.status(500).json({ error: 'Failed to fetch spell' });
    35	    }
    36	});
    37	
    38	// Character routes
    39	app.get('/api/characters', async (req, res) => {
    40	    try {
    41	        const characters = await dataStore.getAllCharacters();
    42	        res.json({ characters });
    43	    } catch (error) {
