     1	import sqlite3 from 'sqlite3';
     2	import { Spell, SpellConvocation, BonusEffect } from '../models/Spell.js';
     3	import { Character } from '../models/Character.js';
     4	
     5	export class Database {
     6	    private db: sqlite3.Database;
     7	
     8	    constructor(dbPath: string = './grimoire.db') {
     9	        this.db = new sqlite3.Database(dbPath);
    10	        this.initializeTables();
    11	    }
    12	
    13	    private initializeTables(): void {
    14	        this.db.serialize(() => {
    15	            this.db.run(`
    16	                CREATE TABLE IF NOT EXISTS spells (
    17	                    id TEXT PRIMARY KEY,
    18	                    name TEXT NOT NULL,
    19	                    convocation TEXT NOT NULL,
    20	                    complexity_level INTEGER NOT NULL,
    21	                    description TEXT NOT NULL,
    22	                    bonus_effects TEXT NOT NULL,
    23	                    casting_time TEXT NOT NULL,
    24	                    range TEXT NOT NULL,
    25	                    duration TEXT NOT NULL
    26	                )
    27	            `);
    28	
    29	            this.db.run(`
    30	                CREATE TABLE IF NOT EXISTS characters (
    31	                    id TEXT PRIMARY KEY,
    32	                    name TEXT NOT NULL,
    33	                    occupation TEXT NOT NULL,
    34	                    game TEXT NOT NULL
    35	                )
    36	            `);
    37	
    38	            this.db.run(`
    39	                CREATE TABLE IF NOT EXISTS character_spells (
    40	                    character_id TEXT,
    41	                    spell_id TEXT,
    42	                    PRIMARY KEY (character_id, spell_id),
    43	                    FOREIGN KEY (character_id) REFERENCES characters(id) ON DELETE CASCADE,
    44	                    FOREIGN KEY (spell_id) REFERENCES spells(id) ON DELETE CASCADE
    45	                )
    46	            `);
    47	        });
    48	    }
    49	
    50	    // Spell methods
    51	    getAllSpells(): Promise<Spell[]> {
    52	        return new Promise((resolve, reject) => {
    53	            this.db.all('SELECT * FROM spells', (err, rows: any[]) => {
    54	                if (err) reject(err);
    55	                else resolve(rows.map(this.rowToSpell));
    56	            });
    57	        });
    58	    }
    59	
    60	    getSpellById(id: string): Promise<Spell | undefined> {
    61	        return new Promise((resolve, reject) => {
    62	            this.db.get('SELECT * FROM spells WHERE id = ?', [id], (err, row: any) => {
    63	                if (err) reject(err);
    64	                else resolve(row ? this.rowToSpell(row) : undefined);
    65	            });
    66	        });
    67	    }
    68	
    69	    addSpell(spell: Spell): Promise<void> {
    70	        return new Promise((resolve, reject) => {
    71	            const stmt = this.db.prepare(`
    72	                INSERT INTO spells (id, name, convocation, complexity_level, description, bonus_effects, casting_time, range, duration)
    73	                VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
    74	            `);
    75	            stmt.run([
    76	                spell.id,
    77	                spell.name,
    78	                spell.convocation,
    79	                spell.complexityLevel,
    80	                spell.description,
    81	                JSON.stringify(spell.bonusEffects),
    82	                spell.castingTime,
    83	                spell.range,
    84	                spell.duration
    85	            ], (err) => {
    86	                if (err) reject(err);
    87	                else resolve();
    88	            });
    89	            stmt.finalize();
    90	        });
    91	    }
    92	
    93	    // Character methods
    94	    getAllCharacters(): Promise<Character[]> {
    95	        return new Promise((resolve, reject) => {
    96	            this.db.all('SELECT * FROM characters', async (err, rows: any[]) => {
    97	                if (err) reject(err);
    98	                else {
    99	                    const characters = await Promise.all(
   100	                        rows.map(async (row) => await this.rowToCharacter(row))
   101	                    );
   102	                    resolve(characters);
   103	                }
   104	            });
   105	        });
   106	    }
   107	
   108	    getCharacterById(id: string): Promise<Character | undefined> {
   109	        return new Promise((resolve, reject) => {
   110	            this.db.get('SELECT * FROM characters WHERE id = ?', [id], async (err, row: any) => {
   111	                if (err) reject(err);
   112	                else resolve(row ? await this.rowToCharacter(row) : undefined);
   113	            });
   114	        });
   115	    }
   116	
   117	    addCharacter(character: Character): Promise<void> {
   118	        return new Promise((resolve, reject) => {
   119	            this.db.serialize(() => {
   120	                const stmt = this.db.prepare(`
   121	                    INSERT INTO characters (id, name, occupation, game)
   122	                    VALUES (?, ?, ?, ?)
   123	                `);
   124	                stmt.run([character.id, character.name, character.occupation, character.game], (err) => {
   125	                    if (err) {
   126	                        reject(err);
   127	                        return;
   128	                    }
   129	                    
   130	                    // Add spell relationships
   131	                    if (character.knownSpellIds.length > 0) {
   132	                        const spellStmt = this.db.prepare('INSERT INTO character_spells (character_id, spell_id) VALUES (?, ?)');
   133	                        character.knownSpellIds.forEach(spellId => {
   134	                            spellStmt.run([character.id, spellId]);
   135	                        });
   136	                        spellStmt.finalize();
   137	                    }
   138	                    resolve();
   139	                });
   140	                stmt.finalize();
   141	            });
   142	        });
   143	    }
   144	
   145	    getSpellsForCharacter(characterId: string): Promise<Spell[]> {
   146	        return new Promise((resolve, reject) => {
   147	            const query = `
   148	                SELECT s.* FROM spells s
   149	                JOIN character_spells cs ON s.id = cs.spell_id
   150	                WHERE cs.character_id = ?
   151	            `;
   152	            this.db.all(query, [characterId], (err, rows: any[]) => {
   153	                if (err) reject(err);
   154	                else resolve(rows.map(this.rowToSpell));
   155	            });
   156	        });
   157	    }
   158	
   159	    private rowToSpell(row: any): Spell {
   160	        return {
   161	            id: row.id,
   162	            name: row.name,
   163	            convocation: row.convocation as SpellConvocation,
   164	            complexityLevel: row.complexity_level,
   165	            description: row.description,
   166	            bonusEffects: JSON.parse(row.bonus_effects) as BonusEffect[],
   167	            castingTime: row.casting_time,
   168	            range: row.range,
   169	            duration: row.duration
   170	        };
   171	    }
   172	
   173	    private async rowToCharacter(row: any): Promise<Character> {
   174	        const knownSpellIds = await this.getKnownSpellIds(row.id);
   175	        return {
   176	            id: row.id,
   177	            name: row.name,
   178	            occupation: row.occupation,
   179	            game: row.game,
   180	            knownSpellIds
   181	        };
   182	    }
   183	
   184	    private getKnownSpellIds(characterId: string): Promise<string[]> {
   185	        return new Promise((resolve, reject) => {
   186	            this.db.all(
   187	                'SELECT spell_id FROM character_spells WHERE character_id = ?',
   188	                [characterId],
   189	                (err, rows: any[]) => {
   190	                    if (err) reject(err);
   191	                    else resolve(rows.map(row => row.spell_id));
   192	                }
   193	            );
   194	        });
   195	    }
   196	
   197	    close(): void {
   198	        this.db.close();
   199	    }
   200	}
   201	
   202	export const database = new Database();
