    10	export function SpellCard({ spell, size = 'medium', onRemove, onClick }: SpellCardProps) {
    11	  const sizeClasses = {
    12	    small: 'w-48 h-32',
    13	    medium: 'w-64 h-40',
    14	    large: 'w-80 h-48'
    15	  }
    16	
    17	  const textSizeClasses = {
    18	    small: {
    19	      title: 'text-sm',
    20	      subtitle: 'text-xs',
    21	      description: 'text-xs',
    22	      details: 'text-xs'
    23	    },
    24	    medium: {
    25	      title: 'text-base',
    26	      subtitle: 'text-sm',
    27	      description: 'text-sm',
    28	      details: 'text-xs'
    29	    },
    30	    large: {
    31	      title: 'text-lg',
    32	      subtitle: 'text-base',
    33	      description: 'text-sm',
    34	      details: 'text-sm'
    35	    }
    36	  }
    37	
    38	  const textClasses = textSizeClasses[size]
    39	
    40	  return (
    41	    <div className={`${sizeClasses[size]} bg-gray-800 border border-gray-700 rounded-lg p-3 hover:border-gray-600 transition-colors relative group flex flex-col`} onClick={onClick}>
