    43	      {onRemove && (
    44	        <button
    45	          onClick={onRemove}
    46	          className="absolute top-2 right-2 w-6 h-6 bg-red-600 hover:bg-red-700 text-white rounded-full text-xs opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center"
    47	          title="Remove spell from character"
    48	        >
    49	          ×
    50	        </button>
    51	      )}
    52	
    53	      {/* Header */}
    54	      <div className="mb-2">
    55	        <h3 className={`${textClasses.title} font-semibold text-white truncate pr-8`}>
    56	          {spell.name}
    57	        </h3>
    58	        <div className={`${textClasses.subtitle} text-gray-400`}>
    59	          {spell.convocation} • Level {spell.complexityLevel}
    60	        </div>
    61	      </div>
    62	
    63	      {/* Description */}
    64	      <div className="flex-1 overflow-hidden mb-2">
    65	        <p className={`${textClasses.description} text-gray-300 line-clamp-3`}>
    66	          {spell.description}
    67	        </p>
    68	      </div>
    69	
    70	      {/* Footer details */}
    71	      <div className={`${textClasses.details} text-gray-500 space-y-1`}>
    72	        {spell.castingTime && (
    73	          <div className="truncate">
    74	            <span className="font-medium">Cast:</span> {spell.castingTime}
    75	          </div>
    76	        )}
    77	        {spell.range && (
    78	          <div className="truncate">
    79	            <span className="font-medium">Range:</span> {spell.range}
    80	          </div>
    81	        )}
