   122	app.delete('/api/spells/:id', async (req, res) => {
   123	    try {
   124	        const spellId = req.params.id;
   125	
   126	        // Check if spell exists
   127	        const existingSpell = await dataStore.getSpellById(spellId);
   128	        if (!existingSpell) {
