    12	
    13	export function MainContent({
    14	  selectedCharacter,
    15	  onAddSpellToCharacter,
    16	  onRemoveSpellFromCharacter,
    17	  refreshTrigger
    18	}: MainContentProps) {
    19	  const [spells, setSpells] = useState<Spell[]>([])
    20	  const [filteredSpells, setFilteredSpells] = useState<Spell[]>([])
    21	  const [searchTerm, setSearchTerm] = useState('')
    22	  const [selectedConvocation, setSelectedConvocation] = useState<string>('all')
    23	  const [selectedComplexity, setSelectedComplexity] = useState<string>('all')
    24	  const [loading, setLoading] = useState(false)
    25	  const [isDragOver, setIsDragOver] = useState(false)
    26	  const [selectedSpell, setSelectedSpell] = useState<Spell | null>(null)
    27	
    28	  const convocations = Object.values(SpellConvocation)
    29	
    30	  useEffect(() => {
    31	    if (selectedCharacter) {
    32	      fetchCharacterSpells(selectedCharacter.id)
    33	    } else {
    34	      setSpells([])
    35	      setFilteredSpells([])
    36	    }
    37	  }, [selectedCharacter, refreshTrigger]) // Add refreshTrigger as dependency
    38	
    39	  useEffect(() => {
    40	    filterSpells()
    41	  }, [spells, searchTerm, selectedConvocation, selectedComplexity])
    42	
    43	  const fetchCharacterSpells = async (characterId: string) => {
    44	    setLoading(true)
    45	    try {
    46	      const response = await fetch(`/api/characters/${characterId}/spells`)
    47	      const data = await response.json()
    48	      setSpells(data.spells || [])
    49	    } catch (error) {
    50	      console.error('Failed to fetch character spells:', error)
    51	      setSpells([])
    52	    } finally {
    53	      setLoading(false)
    54	    }
    55	  }
    56	
    57	  const filterSpells = () => {
    58	    let filtered = spells
    59	
    60	    if (searchTerm) {
    61	      filtered = filtered.filter(spell =>
    62	        spell.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    63	        spell.description.toLowerCase().includes(searchTerm.toLowerCase())
    64	      )
    65	    }
    66	
    67	    if (selectedConvocation !== 'all') {
    68	      filtered = filtered.filter(spell => spell.convocation === selectedConvocation)
    69	    }
    70	
    71	    if (selectedComplexity !== 'all') {
    72	      filtered = filtered.filter(spell => spell.complexityLevel.toString() === selectedComplexity)
    73	    }
    74	
    75	    setFilteredSpells(filtered)
    76	  }
    77	
    78	  const handleDragOver = (e: React.DragEvent) => {
    79	    e.preventDefault()
    80	    e.stopPropagation()
    81	    e.dataTransfer.dropEffect = 'copy'
    82	    setIsDragOver(true)
    83	  }
    84	
    85	  const handleDragLeave = (e: React.DragEvent) => {
    86	    e.preventDefault()
    87	    setIsDragOver(false)
    88	  }
