    11	export function SpellCard({ spell, size = 'medium', onRemove, onClick }: SpellCardProps) {
    12	  const sizeClasses = {
    13	    small: 'w-48 h-32',
    14	    medium: 'w-64 h-40',
    15	    large: 'w-80 h-48'
    16	  }
