    15	/**
    16	 * Builds a hierarchical folder tree from a flat list of spells and empty folders
    17	 */
    18	export function buildFolderTree(spells: Spell[], expandedState: FolderTreeState = {}, emptyFolders: string[] = []): FolderNode {
    19	  const root: FolderNode = {
    20	    name: '',
    21	    path: '/',
    22	    children: [],
    23	    spells: [],
    24	    isExpanded: true
    25	  }
    26	
    27	  // Group spells by folder path
    28	  const folderMap = new Map<string, Spell[]>()
    29	  
    30	  spells.forEach(spell => {
    31	    const folderPath = spell.folderPath || '/'
    32	    if (!folderMap.has(folderPath)) {
    33	      folderMap.set(folderPath, [])
    34	    }
    35	    folderMap.get(folderPath)!.push(spell)
    36	  })
...
    45	
    46	  allPaths.forEach(path => {
    47	    if (path === '/') {
    48	      // Root level spells
    49	      root.spells = folderMap.get(path) || []
    50	      return
    51	    }
    52	
    53	    // Create nodes for this path and all parent paths
    54	    const pathParts = path.split('/').filter(Boolean)
    55	    let currentPath = ''
    56	    
    57	    pathParts.forEach((part, index) => {
    58	      const parentPath = currentPath || '/'
    59	      currentPath = currentPath + '/' + part
    60	      
    61	      if (!nodeMap.has(currentPath)) {
    62	        const node: FolderNode = {
    63	          name: part,
    64	          path: currentPath,
    65	          children: [],
    66	          spells: [],
    67	          isExpanded: expandedState[currentPath] ?? false
    68	        }
    69	        
    70	        nodeMap.set(currentPath, node)
    71	        
    72	        // Add to parent
    73	        const parent = nodeMap.get(parentPath)!
    74	        parent.children.push(node)
    75	      }
    76	    })
    77	
    78	    // Add spells to the final node
    79	    const finalNode = nodeMap.get(path)
    80	    if (finalNode) {
    81	      finalNode.spells = folderMap.get(path) || []
    82	    }
    83	  })
    84	
    85	  // Sort children at each level
    86	  const sortNode = (node: FolderNode) => {
    87	    node.children.sort((a, b) => a.name.localeCompare(b.name))
    88	    node.spells.sort((a, b) => a.name.localeCompare(b.name))
    89	    node.children.forEach(sortNode)
    90	  }
    91	  
    92	  sortNode(root)
    93	  return root
    94	}
