     5	
     6	interface SpellSidebarProps {
     7	  spells: Spell[]
     8	  selectedSpell: Spell | null
     9	  onSpellSelect: (spell: Spell) => void
    10	  onSpellsChange: () => void
    11	  onAddSpell: () => void
    12	  onEditSpell: () => void
    13	  onDeleteSpell: () => void
    14	  onAddSpellToCharacter?: (spell: Spell) => void
    15	  hasSelectedCharacter: boolean
    16	  loading: boolean
    17	  onUpdateSpell?: (spell: Spell) => void // For moving spells between folders
    18	}
    19	
    20	export function SpellSidebar({
    21	  spells,
    22	  selectedSpell,
    23	  onSpellSelect,
    24	  onSpellsChange,
    25	  onAddSpell,
    26	  onEditSpell,
    27	  onDeleteSpell,
    28	  onAddSpellToCharacter,
    29	  hasSelectedCharacter,
    30	  loading,
    31	  onUpdateSpell
    32	}: SpellSidebarProps) {
    33	
    34	  const [expandedFolders, setExpandedFolders] = useState<FolderTreeState>({
    35	    '/': true // Root is always expanded
    36	  })
    37	  const [emptyFolders, setEmptyFolders] = useState<string[]>([])
    38	
    39	  // Fetch empty folders
    40	  useEffect(() => {
    41	    const fetchFolders = async () => {
    42	      try {
    43	        const response = await fetch('/api/folders')
    44	        const data = await response.json()
    45	        setEmptyFolders(data.folders || [])
    46	      } catch (error) {
    47	        console.error('Failed to fetch folders:', error)
    48	      }
    49	    }
    50	
    51	    fetchFolders()
    52	  }, [spells]) // Refetch when spells change
    53	
    54	  // Build the folder tree from spells and empty folders
    55	  const folderTree = useMemo(() => {
    56	    return buildFolderTree(spells, expandedFolders, emptyFolders)
    57	  }, [spells, expandedFolders, emptyFolders])
    58	
    59	  const handleToggleFolder = (path: string) => {
    60	    setExpandedFolders(prev => ({
    61	      ...prev,
    62	      [path]: !prev[path]
    63	    }))
    64	  }
    65	
    66	  const handleMoveSpell = async (spellId: string, newFolderPath: string) => {
    67	    const spell = spells.find(s => s.id === spellId)
    68	    if (!spell || !onUpdateSpell) return
    69	
    70	    const updatedSpell = { ...spell, folderPath: newFolderPath }
    71	    onUpdateSpell(updatedSpell)
    72	  }
    73	
    74	  const handleCreateFolder = async (parentPath: string, folderName: string) => {
    75	    try {
    76	      const newFolderPath = parentPath === '/' ? `/${folderName}` : `${parentPath}/${folderName}`
    77	
    78	      const response = await fetch('/api/folders', {
    79	        method: 'POST',
    80	        headers: {
    81	          'Content-Type': 'application/json',
    82	        },
    83	        body: JSON.stringify({ folderPath: newFolderPath }),
    84	      })
    85	
    86	      if (!response.ok) {
    87	        throw new Error('Failed to create folder')
    88	      }
    89	
    90	      // Expand the parent folder and the new folder
    91	      setExpandedFolders(prev => ({
    92	        ...prev,
    93	        [parentPath]: true,
    94	        [newFolderPath]: true
    95	      }))
    96	
    97	      // Refresh the spell list to pick up the new folder
    98	      onSpellsChange()
    99	    } catch (error) {
   100	      console.error('Failed to create folder:', error)
   101	    }
   102	  }
...
   157	  return (
   158	    <div className="w-80 flex-shrink-0 bg-gray-800 border-l border-gray-700 flex flex-col">
   159	      <div className="p-4 border-b border-gray-700 flex justify-between items-center h-[76px]">
   160	        <h2 className="text-xl font-semibold">Spells</h2>
   161	        <div className="flex gap-2">
   162	          <button 
   163	            onClick={onAddSpell}
   164	            className="bg-gray-700 hover:bg-gray-600 text-white px-3 py-2 rounded text-sm transition-colors" 
   165	            title="Add Spell"
   166	          >
   167	            +
   168	          </button>
   169	          <button 
   170	            onClick={onEditSpell}
   171	            disabled={!selectedSpell}
   172	            className="bg-gray-700 hover:bg-gray-600 disabled:bg-gray-800 disabled:cursor-not-allowed disabled:opacity-50 text-white px-3 py-2 rounded text-sm transition-colors" 
   173	            title={selectedSpell ? "Edit Spell" : "Select a spell to edit"}
   174	          >
   175	            ✏️
   176	          </button>
   177	          <button 
   178	            onClick={onDeleteSpell}
...
   187	      
   188	      <div className="flex-1 overflow-y-auto">
   189	        {loading ? (
   190	          <div className="flex items-center justify-center p-8 text-gray-400 italic">
   191	            Loading spells...
   192	          </div>
   193	        ) : spells.length === 0 ? (
   194	          <div className="flex items-center justify-center p-8 text-gray-400 italic">
   195	            No spells found
   196	          </div>
   197	        ) : (
   198	          <FolderTreeNode
   199	            node={folderTree}
   200	            selectedSpell={selectedSpell}
   201	            onSpellSelect={onSpellSelect}
   202	            onToggleFolder={handleToggleFolder}
   203	            onAddSpellToCharacter={onAddSpellToCharacter}
   204	            hasSelectedCharacter={hasSelectedCharacter}
   205	            level={0}
   206	            onCreateFolder={handleCreateFolder}
   207	            onRenameFolder={handleRenameFolder}
   208	            onDeleteFolder={handleDeleteFolder}
   209	            onMoveSpell={handleMoveSpell}
   210	          />
   211	        )}
   212	      </div>
