     3	interface ModalProps {
     4	  isOpen: boolean
     5	  onClose: () => void
     6	  title: string
     7	  children: ReactNode
     8	}
     9	
    10	export function Modal({ isOpen, onClose, title, children }: ModalProps) {
    11	  useEffect(() => {
    12	    const handleEscape = (e: KeyboardEvent) => {
    13	      if (e.key === 'Escape') {
    14	        onClose()
    15	      }
    16	    }
    17	
    18	    if (isOpen) {
    19	      document.addEventListener('keydown', handleEscape)
    20	      document.body.style.overflow = 'hidden'
    21	    }
    22	
    23	    return () => {
    24	      document.removeEventListener('keydown', handleEscape)
    25	      document.body.style.overflow = 'unset'
    26	    }
    27	  }, [isOpen, onClose])
    28	
    29	  if (!isOpen) return null
    30	
    31	  return (
    32	    <div className="fixed inset-0 z-50 flex items-center justify-center">
    33	      {/* Backdrop */}
    34	      <div 
    35	        className="absolute inset-0 bg-black bg-opacity-50"
    36	        onClick={onClose}
    37	      />
