    12	
    13	    private initializeTables(): void {
    14	        this.db.serialize(() => {
    15	            this.db.run(`
    16	                CREATE TABLE IF NOT EXISTS spells (
    17	                    id TEXT PRIMARY KEY,
    18	                    name TEXT NOT NULL,
    19	                    convocation TEXT NOT NULL,
    20	                    complexity_level INTEGER NOT NULL,
    21	                    description TEXT NOT NULL,
    22	                    bonus_effects TEXT NOT NULL,
    23	                    casting_time TEXT NOT NULL,
    24	                    range TEXT NOT NULL,
    25	                    duration TEXT NOT NULL,
    26	                    folder_path TEXT NOT NULL DEFAULT '/',
    27	                    source_book TEXT NOT NULL DEFAULT '',
    28	                    source_page INTEGER NOT NULL DEFAULT 0
    29	                )
    30	            `);
    31	
    32	            this.db.run(`
    33	                CREATE TABLE IF NOT EXISTS characters (
    34	                    id TEXT PRIMARY KEY,
    35	                    name TEXT NOT NULL,
    36	                    convocations TEXT NOT NULL,
    37	                    rank TEXT NOT NULL,
    38	                    game TEXT NOT NULL
    39	                )
    40	            `);
    41	
    42	            this.db.run(`
    43	                CREATE TABLE IF NOT EXISTS character_spells (
    44	                    character_id TEXT,
    45	                    spell_id TEXT,
    46	                    PRIMARY KEY (character_id, spell_id),
    47	                    FOREIGN KEY (character_id) REFERENCES characters(id) ON DELETE CASCADE,
    48	                    FOREIGN KEY (spell_id) REFERENCES spells(id) ON DELETE CASCADE
    49	                )
    50	            `);
    51	
    52	            this.db.run(`
    53	                CREATE TABLE IF NOT EXISTS folders (
    54	                    folder_path TEXT PRIMARY KEY,
    55	                    created_at DATETIME DEFAULT CURRENT_TIMESTAMP
    56	                )
    57	            `);
    58	        });
    59	    }
    60	
    61	    // Spell methods
    62	    getAllSpells(): Promise<Spell[]> {
    63	        return new Promise((resolve, reject) => {
    64	            this.db.all('SELECT * FROM spells', (err, rows: any[]) => {
    65	                if (err) reject(err);
    66	                else resolve(rows.map(this.rowToSpell));
    67	            });
    68	        });
    69	    }
