    71	    getSpellById(id: string): Promise<Spell | undefined> {
    72	        return new Promise((resolve, reject) => {
    73	            this.db.get('SELECT * FROM spells WHERE id = ?', [id], (err, row: any) => {
    74	                if (err) reject(err);
    75	                else resolve(row ? this.rowToSpell(row) : undefined);
    76	            });
    77	        });
