    80	    addSpell(spell: Spell): Promise<void> {
    81	        return new Promise((resolve, reject) => {
    82	            const stmt = this.db.prepare(`
    83	                INSERT INTO spells (id, name, convocation, complexity_level, description, bonus_effects, casting_time, range, duration, folder_path, source_book, source_page)
    84	                VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
    85	            `);
    86	            stmt.run([
    87	                spell.id,
    88	                spell.name,
    89	                spell.convocation,
    90	                spell.complexityLevel,
    91	                spell.description,
    92	                JSON.stringify(spell.bonusEffects),
    93	                spell.castingTime,
    94	                spell.range,
    95	                spell.duration,
    96	                spell.folderPath || '/',
    97	                spell.sourceBook || '',
    98	                spell.sourcePage || 0
    99	            ], (err) => {
   100	                if (err) reject(err);
   101	                else resolve();
   102	            });
   103	            stmt.finalize();
   104	        });
   105	    }
