   137	    deleteSpell(id: string): Promise<void> {
   138	        return new Promise((resolve, reject) => {
   139	            this.db.serialize(() => {
   140	                // First delete character-spell relationships
   141	                this.db.run('DELETE FROM character_spells WHERE spell_id = ?', [id], (err) => {
   142	                    if (err) {
   143	                        reject(err);
   144	                        return;
   145	                    }
   146	
   147	                    // Then delete the spell
   148	                    this.db.run('DELETE FROM spells WHERE id = ?', [id], (deleteErr) => {
   149	                        if (deleteErr) {
   150	                            reject(deleteErr);
   151	                            return;
   152	                        }
   153	                        resolve();
   154	                    });
   155	                });
   156	            });
   157	        });
   158	    }
