   234	
   235	    deleteFolder(folderPath: string): Promise<void> {
   236	        return new Promise((resolve, reject) => {
   237	            this.db.serialize(() => {
   238	                // Move all spells in this folder to parent folder
   239	                const parentPath = folderPath.lastIndexOf('/') > 0
   240	                    ? folderPath.substring(0, folderPath.lastIndexOf('/'))
   241	                    : '/';
   242	
   243	                this.db.run(
   244	                    'UPDATE spells SET folder_path = ? WHERE folder_path = ?',
   245	                    [parentPath, folderPath],
   246	                    (err) => {
   247	                        if (err) {
   248	                            reject(err);
   249	                            return;
   250	                        }
   251	
   252	                        // Delete the folder record
   253	                        this.db.run('DELETE FROM folders WHERE folder_path = ?', [folderPath], (deleteErr) => {
   254	                            if (deleteErr) {
   255	                                reject(deleteErr);
   256	                                return;
   257	                            }
   258	                            resolve();
   259	                        });
   260	                    }
   261	                );
   262	            });
   263	        });
   264	    }
