   377	    getSpellsForCharacter(characterId: string): Promise<Spell[]> {
   378	        return new Promise((resolve, reject) => {
   379	            const query = `
   380	                SELECT s.* FROM spells s
   381	                JOIN character_spells cs ON s.id = cs.spell_id
   382	                WHERE cs.character_id = ?
   383	            `;
