   391	    private rowToSpell(row: any): Spell {
   392	        return {
   393	            id: row.id,
   394	            name: row.name,
   395	            convocation: row.convocation as SpellConvocation,
   396	            complexityLevel: row.complexity_level,
   397	            description: row.description,
   398	            bonusEffects: JSON.parse(row.bonus_effects) as BonusEffect[],
   399	            castingTime: row.casting_time,
   400	            range: row.range,
   401	            duration: row.duration,
   402	            folderPath: row.folder_path || '/',
   403	            sourceBook: row.source_book || '',
   404	            sourcePage: row.source_page || 0
   405	        };
   406	    }
   407	
   408	    private async rowToCharacter(row: any): Promise<Character> {
   409	        const knownSpellIds = await this.getKnownSpellIds(row.id);
   410	        return {
   411	            id: row.id,
   412	            name: row.name,
   413	            convocations: JSON.parse(row.convocations) as SpellConvocation[],
   414	            rank: row.rank as CharacterRank,
   415	            game: row.game,
   416	            knownSpellIds
   417	        };
   418	    }
   419	
   420	    private getKnownSpellIds(characterId: string): Promise<string[]> {
   421	        return new Promise((resolve, reject) => {
   422	            this.db.all(
   423	                'SELECT spell_id FROM character_spells WHERE character_id = ?',
   424	                [characterId],
   425	                (err, rows: any[]) => {
   426	                    if (err) reject(err);
   427	                    else resolve(rows.map(row => row.spell_id));
   428	                }
   429	            );
   430	        });
   431	    }
   432	
   433	    close(): void {
   434	        this.db.close();
   435	    }
   436	}
