    38	
    39	app.post('/api/spells', async (req, res) => {
    40	    try {
    41	        const { name, convocation, complexityLevel, description, bonusEffects, castingTime, range, duration, folderPath } = req.body;
    42	
    43	        if (!name) {
    44	            return res.status(400).json({ error: 'Spell name is required' });
    45	        }
    46	        if (!convocation) {
    47	            return res.status(400).json({ error: 'Spell convocation is required' });
    48	        }
    49	        if (!complexityLevel || complexityLevel < 1) {
    50	            return res.status(400).json({ error: 'Valid complexity level is required (minimum 1)' });
    51	        }
    52	        if (!description) {
    53	            return res.status(400).json({ error: 'Spell description is required' });
    54	        }
    55	
    56	        const spell = {
    57	            id: crypto.randomUUID(),
    58	            name,
    59	            convocation,
    60	            complexityLevel: parseInt(complexityLevel),
    61	            description,
    62	            bonusEffects: bonusEffects || [],
    63	            castingTime: castingTime || '',
    64	            range: range || '',
    65	            duration: duration || '',
    66	            folderPath: folderPath || '/',
    67	            sourceBook: '',
    68	            sourcePage: 0
    69	        };
    70	
    71	        await dataStore.addSpell(spell);
    72	        res.status(201).json(spell);
    73	    } catch (error) {
    74	        res.status(500).json({ error: 'Failed to create spell' });
    75	    }
    76	});
