Path: apps/frontend/src/components/FolderPicker.tsx
     1	import { useState, useEffect } from 'react'
     2	
     3	interface FolderPickerProps {
     4	  value: string
     5	  onChange: (folderPath: string) => void
     6	  disabled?: boolean
     7	  allFolders: string[] // All existing folder paths
     8	}
     9	
    10	export function FolderPicker({ value, onChange, disabled = false, allFolders }: FolderPickerProps) {
    11	  const [isCreatingNew, setIsCreatingNew] = useState(false)
    12	  const [newFolderPath, setNewFolderPath] = useState('')
    13	  const [showDropdown, setShowDropdown] = useState(false)
    14	
    15	  // Get unique folder paths and sort them
    16	  const uniqueFolders = Array.from(new Set(allFolders))
    17	    .filter(path => path !== '/')
    18	    .sort()
    19	
    20	  // Add root folder
    21	  const folderOptions = ['/', ...uniqueFolders]
    22	
    23	  const handleSelectFolder = (folderPath: string) => {
    24	    onChange(folderPath)
    25	    setShowDropdown(false)
    26	    setIsCreatingNew(false)
    27	  }
    28	
    29	  const handleCreateNew = () => {
    30	    if (newFolderPath.trim()) {
    31	      let cleanPath = newFolderPath.trim()
    32	      
    33	      // Ensure it starts with /
    34	      if (!cleanPath.startsWith('/')) {
    35	        cleanPath = '/' + cleanPath
    36	      }
    37	      
    38	      // Remove trailing slash unless it's root
    39	      if (cleanPath !== '/' && cleanPath.endsWith('/')) {
    40	        cleanPath = cleanPath.slice(0, -1)
    41	      }
    42	      
    43	      onChange(cleanPath)
    44	      setNewFolderPath('')
    45	      setIsCreatingNew(false)
    46	      setShowDropdown(false)
    47	    }
    48	  }
    49	
    50	  const handleKeyPress = (e: React.KeyboardEvent) => {
    51	    if (e.key === 'Enter') {
    52	      e.preventDefault()
    53	      handleCreateNew()
    54	    } else if (e.key === 'Escape') {
    55	      setIsCreatingNew(false)
    56	      setNewFolderPath('')
    57	    }
    58	  }
    59	
    60	  return (
    61	    <div className="relative">
    62	      <label className="block text-sm font-medium text-gray-300 mb-1">
    63	        Folder Path
    64	      </label>
...
    77	          
    78	          {/* Dropdown */}
    79	          {showDropdown && !disabled && (
    80	            <div className="absolute top-full left-0 right-0 mt-1 bg-gray-700 border border-gray-600 rounded shadow-lg z-10 max-h-48 overflow-y-auto">
    81	              {folderOptions.map(folder => (
    82	                <button
    83	                  key={folder}
    84	                  type="button"
    85	                  onClick={() => handleSelectFolder(folder)}
    86	                  className="w-full text-left px-3 py-2 hover:bg-gray-600 text-white text-sm border-b border-gray-600 last:border-b-0"
    87	                >
    88	                  {folder === '/' ? '/ (root)' : folder}
    89	                </button>
    90	              ))}
    91	              
    92	              {/* Create new folder option */}
    93	              <button
    94	                type="button"
    95	                onClick={() => setIsCreatingNew(true)}
    96	                className="w-full text-left px-3 py-2 hover:bg-gray-600 text-blue-400 text-sm font-medium"
    97	              >
    98	                + Create new folder
    99	              </button>
   100	            </div>
