Path: apps/backend/data/database.ts
...
   181	
   182	    getAllFolders(): Promise<string[]> {
   183	        return new Promise((resolve, reject) => {
   184	            // Get folders from both the folders table and spell folder paths
   185	            this.db.all(`
   186	                SELECT DISTINCT folder_path FROM (
   187	                    SELECT folder_path FROM folders
   188	                    UNION
   189	                    SELECT DISTINCT folder_path FROM spells WHERE folder_path != '/'
   190	                ) ORDER BY folder_path
   191	            `, (err, rows: any[]) => {
   192	                if (err) {
   193	                    reject(err);
   194	                } else {
   195	                    resolve(rows.map(row => row.folder_path));
   196	                }
   197	            });
   198	        });
   199	    }
   200	
   201	    createFolder(folderPath: string): Promise<void> {
   202	        return new Promise((resolve, reject) => {
   203	            const stmt = this.db.prepare('INSERT OR IGNORE INTO folders (folder_path) VALUES (?)');
   204	            stmt.run([folderPath], (err) => {
   205	                if (err) reject(err);
   206	                else resolve();
   207	            });
   208	            stmt.finalize();
   209	        });
   210	    }
   211	
   212	    renameFolder(oldPath: string, newPath: string): Promise<void> {
   213	        return new Promise((resolve, reject) => {
   214	            this.db.serialize(() => {
   215	                // Update folder paths in spells
   216	                this.db.run('UPDATE spells SET folder_path = ? WHERE folder_path = ?', [newPath, oldPath], (err) => {
   217	                    if (err) {
   218	                        reject(err);
   219	                        return;
   220	                    }
   221	
   222	                    // Update folder paths in folders table
   223	                    this.db.run('UPDATE folders SET folder_path = ? WHERE folder_path = ?', [newPath, oldPath], (updateErr) => {
   224	                        if (updateErr) {
   225	                            reject(updateErr);
   226	                            return;
   227	                        }
   228	                        resolve();
   229	                    });
   230	                });
   231	            });
   232	        });
   233	    }
