Path: apps/frontend/src/utils/folderTree.ts
     1	import type { Spell } from '../types'
     2	
     3	export interface FolderNode {
     4	  name: string
     5	  path: string
     6	  children: FolderNode[]
     7	  spells: Spell[]
     8	  isExpanded: boolean
     9	}
    10	
    11	export interface FolderTreeState {
    12	  [path: string]: boolean // expanded state for each folder
    13	}
    14	
    15	/**
    16	 * Builds a hierarchical folder tree from a flat list of spells and empty folders
    17	 */
    18	export function buildFolderTree(spells: Spell[], expandedState: FolderTreeState = {}, emptyFolders: string[] = []): FolderNode {
    19	  const root: FolderNode = {
    20	    name: '',
    21	    path: '/',
    22	    children: [],
    23	    spells: [],
    24	    isExpanded: true
    25	  }
    26	
    27	  // Group spells by folder path
    28	  const folderMap = new Map<string, Spell[]>()
    29	  
    30	  spells.forEach(spell => {
    31	    const folderPath = spell.folderPath || '/'
    32	    if (!folderMap.has(folderPath)) {
    33	      folderMap.set(folderPath, [])
    34	    }
    35	    folderMap.get(folderPath)!.push(spell)
    36	  })
    37	
    38	  // Get all unique folder paths (from spells and empty folders) and sort them
    39	  const spellFolderPaths = Array.from(folderMap.keys())
    40	  const allPaths = Array.from(new Set([...spellFolderPaths, ...emptyFolders])).sort()
    41	  
    42	  // Build the tree structure
    43	  const nodeMap = new Map<string, FolderNode>()
    44	  nodeMap.set('/', root)
    45	
    46	  allPaths.forEach(path => {
    47	    if (path === '/') {
    48	      // Root level spells
    49	      root.spells = folderMap.get(path) || []
    50	      return
    51	    }
    52	
    53	    // Create nodes for this path and all parent paths
    54	    const pathParts = path.split('/').filter(Boolean)
    55	    let currentPath = ''
    56	    
    57	    pathParts.forEach((part, index) => {
    58	      const parentPath = currentPath || '/'
    59	      currentPath = currentPath + '/' + part
    60	      
    61	      if (!nodeMap.has(currentPath)) {
    62	        const node: FolderNode = {
    63	          name: part,
    64	          path: currentPath,
    65	          children: [],
    66	          spells: [],
    67	          isExpanded: expandedState[currentPath] ?? false
    68	        }
    69	        
    70	        nodeMap.set(currentPath, node)
    71	        
    72	        // Add to parent
    73	        const parent = nodeMap.get(parentPath)!
    74	        parent.children.push(node)
    75	      }
    76	    })
    77	
    78	    // Add spells to the final node
    79	    const finalNode = nodeMap.get(path)
    80	    if (finalNode) {
    81	      finalNode.spells = folderMap.get(path) || []
    82	    }
    83	  })
    84	
    85	  // Sort children at each level
    86	  const sortNode = (node: FolderNode) => {
    87	    node.children.sort((a, b) => a.name.localeCompare(b.name))
    88	    node.spells.sort((a, b) => a.name.localeCompare(b.name))
    89	    node.children.forEach(sortNode)
    90	  }
    91	  
    92	  sortNode(root)
    93	  return root
    94	}
    95	
    96	/**
    97	 * Gets all folder paths from the tree (excluding root)
    98	 */
    99	export function getAllFolderPaths(node: FolderNode, paths: string[] = []): string[] {
   100	  if (node.path !== '/') {
   101	    paths.push(node.path)
   102	  }
   103	  
   104	  node.children.forEach(child => {
   105	    getAllFolderPaths(child, paths)
   106	  })
   107	  
   108	  return paths
   109	}
   110	
   111	/**
   112	 * Checks if a folder path is empty (has no spells and no children with spells)
   113	 */
   114	export function isFolderEmpty(node: FolderNode): boolean {
   115	  if (node.spells.length > 0) {
   116	    return false
   117	  }
   118	  
   119	  return node.children.every(child => isFolderEmpty(child))
   120	}
   121	
   122	/**
   123	 * Gets the parent path of a given path
   124	 */
   125	export function getParentPath(path: string): string {
   126	  if (path === '/') return '/'
   127	  
   128	  const lastSlash = path.lastIndexOf('/')
   129	  if (lastSlash === 0) return '/'
   130	  
   131	  return path.substring(0, lastSlash)
   132	}
...
   156	
   157	/**
   158	 * Creates a new folder path by combining parent and name
   159	 */
   160	export function createFolderPath(parentPath: string, folderName: string): string {
   161	  const cleanName = folderName.trim()
   162	  if (!cleanName) return parentPath
   163	  
   164	  if (parentPath === '/') {
   165	    return '/' + cleanName
   166	  }
   167	  
   168	  return parentPath + '/' + cleanName
   169	}
